import React from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import './Layout.css'

function Layout(props) {
    const {user, signOut} = this.props;

    return(
        <div className='layout'>
            <Header 
                user={user}
                signOut={signOut}
            />
            <div>
                { props.children }
            </div>
            <Footer />
        </div>
    )
}

export default Layout;
